using Dominio.Helpers;
using Dominio.Models;
using Dominio.Repositories;
using Microsoft.Extensions.Configuration;
using Stubble.Core.Builders;
using System;
using System.Collections.Generic;
using System.IO;
using System.Linq;
using System.Net.Mail;
using System.Reflection;

namespace Infraestructura.Service
{
    public class CorreoHelper : ICorreoHelper
    {
        private readonly string rutaHtmlUsiario = "notificacionUsuarioCreado.html";
        private readonly string rutaHtmlRolCreado = "notificacionRolCreado.html";
        private readonly string rutaHtmlRolEditado = "notificacionRolEditado.html";
        private readonly string rutaHtmlVerificacion = "verificarCorreo.html";
        private readonly string rutaHtmlActulizacionCorreoImportador = "ActulizacionCorreoImportador.html";
        private readonly string rutaHtmlDenegacionAcceso = "notificionRechazoUsuario.html";
        private readonly string rutaAccesosImportador = "gestionAccesosIportador.html";
        private readonly string rutaHtmlSolicitudAcceso = "notificaCorreoTemporal.html";

        private readonly string senasaLogo = "";
        private readonly SmtpClient smtpClient;
        private readonly IConfiguration configuration;
        private readonly IRolRepository rolRepository;

        
        public CorreoHelper(SmtpClient smtpClient, IConfiguration configuration, IRolRepository rolRepository)
        {
            this.smtpClient = smtpClient;
            this.configuration = configuration;
            this.rolRepository = rolRepository;
        }
        public void EnviarCorreoUsuarioCreado(string Usuario, string contrasena, string correoDestino)
        {
            var html = ReadResource(rutaHtmlUsiario);
            html = html.Replace("usuarioId", Usuario);
            html = html.Replace("contrasena", contrasena);
            html = html.Replace("URLPORTAL", configuration.GetValue<string>("AppSettings:DireccionPortal"));
            SendMsj("Nuevo Usuario - Sistema de Pagos", correoDestino, html);
        }
        public void EnviarCorreoRolCreado(string Usuario, string NombreRol)
        {
            string correoDestino = configuration.GetValue<string>("AppSettings:EmailAdmin");
            var html = ReadResource(rutaHtmlRolCreado);
            html = html.Replace("usuarioId", Usuario);
            html = html.Replace("nombreRol", NombreRol);
            html = html.Replace("URLPORTAL", configuration.GetValue<string>("AppSettings:DireccionPortal"));
            SendMsj("Nuevo Rol - Sistema de Pagos", correoDestino, html);
        }
        public void EnviarCorreoRolEditado(string Usuario, string NombreRol)
        {
            string correoDestino = configuration.GetValue<string>("AppSettings:EmailAdmin");
            var html = ReadResource(rutaHtmlRolEditado);
            html = html.Replace("usuarioId", Usuario);
            html = html.Replace("nombreRol", NombreRol);
            html = html.Replace("URLPORTAL", configuration.GetValue<string>("AppSettings:DireccionPortal"));
            SendMsj("Rol Editado - Sistema de Pagos", correoDestino, html);
        }
        void ICorreoHelper.EnviarCorreoParaVerificacion(string correoDestino, string tokenVerificacion)
        {
            var html = ReadResource(rutaHtmlVerificacion);
            var url = configuration.GetValue<string>("AppSettings:VerificarCorreo") + "/" + tokenVerificacion;
            html = html.Replace("URLPORTAL", url);
            SendMsj("Verificación de correo", correoDestino, html);
        }
        void ICorreoHelper.EnviarCorreoParaActulizacion(string correoDestino, string tokenVerificacion, DateTime fechaActulizacion,string correoNuevo)
        {

            var html = ReadResource(rutaHtmlActulizacionCorreoImportador);
            html = html.Replace("correo", correoNuevo);
            html = html.Replace("fechaActulizacion", fechaActulizacion.ToString());
            html = html.Replace("URLPORTAL", configuration.GetValue<string>("AppSettings:DireccionPortal"));
            SendMsj("Actulizacion del Correo Electronico", correoDestino, html);
        }


        private void ConfigurarImagenes(AlternateView htmlView)
        {

            byte[] bitmapData = Convert.FromBase64String(senasaLogo);
            MemoryStream streamBitmap = new MemoryStream(bitmapData);

            htmlView.LinkedResources.Add(new LinkedResource(streamBitmap)
            {
                ContentId = "senasaID"
            });


        }

        public string ReadResource(string name)
        {
            var assembly = Assembly.GetExecutingAssembly();
            string resourcePath = assembly.GetManifestResourceNames()
                    .Single(str => str.EndsWith(name));

            using (Stream stream = assembly.GetManifestResourceStream(resourcePath))
            using (StreamReader reader = new StreamReader(stream))
            {
                return reader.ReadToEnd();
            }
        }

        public Stream GetStream(string name)
        {

            var assembly = Assembly.GetExecutingAssembly();
            string resourcePath = assembly.GetManifestResourceNames()
                    .Single(str => str.EndsWith(name));

            return assembly.GetManifestResourceStream(resourcePath);
        }

        
        public void EnviarCorreoDenegacionAcceso(string correoDestino, string motivo)
        {
            var html = ReadResource(rutaHtmlDenegacionAcceso);
            html = html.Replace("MotivoId", motivo);
            SendMsj("Notificación de acceso", correoDestino, html);
        }

        private void SendMsj(string subject, string correoDestino, string html)
        {
            AlternateView htmlView = AlternateView.CreateAlternateViewFromString(html, null, "text/html");
            ConfigurarImagenes(htmlView);
            MailMessage message = new MailMessage
            {
                From = new MailAddress(configuration.GetValue<string>("Email:Smtp:Username"), "GQ Racing SPORT")
            };
            message.To.Add(correoDestino);
            message.Subject = subject;
            message.AlternateViews.Add(htmlView);
            message.IsBodyHtml = true;
            smtpClient.Send(message);
        }

        public void EnviarCorreoAccesosImportador(Cliente importador)
        {

            var html = ReadResource(rutaAccesosImportador);
            var stubble = new StubbleBuilder().Build();
            html = stubble.Render(html,  new { Importador = importador, Roles = "hoola test", Url = configuration.GetValue<string>("AppSettings:DireccionPortal") });
            SendMsj("Notificación de acceso", importador.Correo, html);
        }
        public void EnviarCorreoModificarSolicitud(List<string> correoDestino, string motivo, string CodigoTemporal)
        {
            var html = ReadResource(rutaHtmlSolicitudAcceso);
            var stubble = new StubbleBuilder().Build();
            html = html.Replace("CodigoTemporalId", CodigoTemporal);
            html = html.Replace("MotivoId", motivo);
            foreach (var item in correoDestino)
            {
                SendMsj("Notificación de acceso", item, html);
            }

        }
       
    }
}
